function Calculation(a, b) {
  let sum = a + b;
  let subtraction = a - b;
  let division = Math.floor(a / b);
  let multiply = a * b;

  console.log(` ${a} addition ${b} is ${sum} . \n
   ${a} subtraction ${b} is ${subtraction}. \n
   ${a} divided ${b} is ${division}. \n 
   ${a} multiply ${b} is ${multiply}.`);
}

module.exports = Calculation;
